import './Create.css';
import Header from '../components/Header';

// eslint-disable-next-line
export default () => {
    const [plants, setPlants] = useState([]);
    const [errors, setErrors] = useState([]);

    const createPlant = (plant) => {
        axios.post('http://localhost:8050/api/plants/new', plant)
            .then(res => {
                console.log(res)
                navigate("/")
            })
            .catch(err => {
                console.log(err)
