                // const errorArr = [];
                // for (const key of Object.keys(errorResponse)) {
                //     errorArr.push(errorResponse[key].message)
                // }
                // setErrors(errorArr);
            })
    }


    return (
        <div className="wrapper">
            <Header />
            <div className="plantForm">
                <h2>Add a new plant !</h2>
                <PlantForm onSubmitProp={createPlant} errors={errors} />
            </div>
        </div>
    );
}
