import React, { useEffect, useState } from 'react';
import { Link } from '@reach/router';
import { Button } from 'semantic-ui-react';
// import SearchBox from '../components/SearchBox';
import Search from '../components/Search';
import axios from 'axios';


const LandingPage = () => {

    const [plants, setPlants] = useState([]);
    const [search, setSearch] = useState([]);

    // const searchPlants = () => {
    // // useEffect(() => {
    //     axios.get("https://cors-anywhere.herokuapp.com/https://trefle.io/api/v1/plants?token=wfOfrblXggceP_xcDAcmmxDOO4XmIyJY1KgXKK0_utA")
    //         .then(res => {
    //             setPlants(res.data.data);
    //         });
    // // }, []);
    // };
    const searchPlants = (e) => {
        // useEffect(() => {
        e.preventDefault();
        axios.get(`https://cors-anywhere.herokuapp.com/https://trefle.io/api/v1/species/search?q=${searchValue}&token=wfOfrblXggceP_xcDAcmmxDOO4XmIyJY1KgXKK0_utA`)
            .then(res => {
                setPlants(res.data.data);
            });
        // }, []);
    };




    return (
        <div className="wrapper">
            <header>
                <h1>Planthood </h1>
                <div className="button">
                    <Button className="ui green basic button large"><Link style={{ "color": "green" }} to={"/new"}>Log In</Link></Button>
                    <Button className="ui green basic button large"><Link style={{ "color": "green" }} to={"/"}>Sign Up</Link></Button>
                </div>
            </header>
            <div className="landingDisplay">

                <form onSubmit={searchPlants}  className="search">
                    <input
                        value={search}
                        onChange={(e)=> setSearch(e.target.value)}
                        type="text"
                    />
                </form>

                {/* <Button onClick={searchPlants} className="teal">Search Plants</Button> */}
                <h2>Search Results</h2>

                <div className="list">
                    {plants.map((value, index) => {
                        return (<h3 className="plantList" plant={value} key={index} > {value.common_name} <img width="100" src={value.image_url} alt="tryin"></img></h3>)
                    })}
                </div>
            </div>
        </div>

    )
}

export default LandingPage
