import React, {useState} from 'react';
import axios from 'axios';
import { Button } from 'semantic-ui-react';

function SearchBox ()  {
    const [loading, setLoading] = useState(true);
    const [plants, setPlants] = useState([]);

    // const search = searchValue => {
    //     setLoading(true);
    // }

    const getPlants = (searchValue) => {
        //This API enables cross-origin requests to anywhere
      axios.get(`https://cors-anywhere.herokuapp.com/https://trefle.io/api/v1/species/search?q=${searchValue}&token=wfOfrblXggceP_xcDAcmmxDOO4XmIyJY1KgXKK0_utA`)
        .then(response => {
            setPlants(response.data.data);
            console.log(response);
            // setLoading(false);
        });

      };

      return (
        <div className="App">  
          <Button className="teal" onClick={getPlants}>Fetch Plants</Button>

          <div className="list">
            {plants.map((value, index) => {
              return (<li plant={value} key={index} > {value.common_name} <img src={value.image_url} alt="tryin"></img></li>)
            })}
          </div>

        </div>
      );

  }
  export default SearchBox;
