import React, {useEffect, useState} from 'react';
import { Link } from '@reach/router';
import { Button } from 'semantic-ui-react';
// import SearchBox from '../components/SearchBox';
import Search from '../components/Search';
import axios from 'axios';


const LandingPage = () => {
    const [loading, setLoading] = useState(true);
    const [plants, setPlants] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);

    useEffect(() => {
        axios.get("https://cors-anywhere.herokuapp.com/https://trefle.io/api/v1/plants?token=wfOfrblXggceP_xcDAcmmxDOO4XmIyJY1KgXKK0_utA")
            .then(res => {
                setPlants(res.data.data);
                setLoading(false);
            });
    }, []);

    const search = searchValue => {
        setLoading(true);
        setErrorMessage(null);

        axios.get(`https://cors-anywhere.herokuapp.com/https://trefle.io/api/v1/species/search?q=${searchValue}&token=wfOfrblXggceP_xcDAcmmxDOO4XmIyJY1KgXKK0_utA`)
        .then(jsonResponse => {
            if (jsonResponse.Response === "True") {
              setPlants(jsonResponse.Search);
              setLoading(false);
            } else {
              setErrorMessage(jsonResponse.Error);
              setLoading(false);
            }
          });

    }

    // const plantFromApi = ({plant}) => {
    //     return (
    //         <div>
    //             <h3>{plant.common_name}</h3>
    //             <div>
    //                 <img width="150" alt="plant" src={plant.image_url}></img>
    //             </div>
    //         </div>
    //     )
    // }

    return (
        <div className="wrapper">
            <header>
                <h1>Planthood </h1>
                <div className="button">
                <Button className="ui green basic button large"><Link style={{"color": "green"}} to={"/new"}>Log In</Link></Button>
                <Button className="ui green basic button large"><Link style={{"color": "green"}} to={"/"}>Sign Up</Link></Button>
                </div>
            </header>
            <div className="landingDisplay">
                <Search search={search} />
                <h2>SEARCHBOX</h2>
                <h2>Search Results</h2>
                <div className="movies">
                {loading && !errorMessage ? (
                <span>loading...</span>
                ) : errorMessage ? (
                <div className="errorMessage">{errorMessage}</div>
                ) : (
                plants.map((value, index) => {
                    return (<li plant={value} key={index} > {value.common_name} <img src={value.image_url} alt="tryin"></img></li>)
                })
                )}
            </div>
            </div>
        </div>
    )
}

export default LandingPage
