import React, { useState } from 'react';
import { Link, navigate } from '@reach/router';
import { Button, Form } from 'semantic-ui-react';
import axios from 'axios';

// eslint-disable-next-line
export default (props) => {
    const { onSubmitProp } = props;
    //keep track of what is being typed via useState hook
    const [name, setName] = useState("");
    const [nickName, setNickName] = useState("");
    const [dateAdopted, setDateAdopted] = useState("");
    const [light, setLight] = useState("");
    const [water, setWater] = useState("");
    // const [errors, setErrors] = useState([]);
    //handler when the form is submitted

    const onSubmitHandler = (e) => {
        e.preventDefault();
        onSubmitProp({ name, nickName, dateAdopted, light, water });
    }

    // const SubmitButton = () => {
    //     if (name.length > 2) {
    //         return <Button className="yellow large" style={{"marginTop": "10px"}} type='submit'>SUBMIT</Button>     
    //     } else {
    //         return <Button disabled className="yellow large" style={{"marginTop": "10px"}} type='submit'>SUBMIT</Button>
    //     };
    // };


    return (
        <div className="contentForm">
            <Form onSubmit={onSubmitHandler}>
                <Form.Field>
                    <label>Plant Name:</label>
                    <input type="text" onChange={(e) => setName(e.target.value)} value={name} />
                </Form.Field>
                <Form.Field>
                    <label>Nickname:</label>
                    <input type="text" onChange={(e) => setNickName(e.target.value)} value={nickName} />
                </Form.Field>
                {/* <SubmitButton /> */}

                <Button className="yellow large" style={{ "marginTop": "10px" }} type='submit'>SUBMIT</Button>
            </Form>

        </div>
    )
}
