import React, { useState } from 'react';
import { Link, navigate } from '@reach/router';
import { Button, Form } from 'semantic-ui-react';
import axios from 'axios';
import './PlantForm.css';

// eslint-disable-next-line
export default (props) => {
    const { onSubmitProp } = props;
    //keep track of what is being typed via useState hook
    const [name, setName] = useState("");
    const [scientificName, setScientificName] = useState("");
    const [nickName, setNickName] = useState("");
    const [dateAdopted, setDateAdopted] = useState("");
    const [light, setLight] = useState("");
    const [water, setWater] = useState("");
    // const [errors, setErrors] = useState([]);
    //handler when the form is submitted

    const onSubmitHandler = (e) => {
        e.preventDefault();
        onSubmitProp({ name, nickName, dateAdopted, light, water });
    }

    // const SubmitButton = () => {
    //     if (name.length > 2) {
    //         return <Button className="yellow large" style={{"marginTop": "10px"}} type='submit'>SUBMIT</Button>     
    //     } else {
    //         return <Button disabled className="yellow large" style={{"marginTop": "10px"}} type='submit'>SUBMIT</Button>
    //     };
    // };


    return (
        <div className="contentForm">
            <Form onSubmit={onSubmitHandler}>
                <Form.Group widths='equal'>
                    <Form.Input fluid label='Name' type="text" onChange={(e) => setName(e.target.value)} value={name} />
                    <Form.Input fluid label='Scientific Name' type="text" onChange={(e) => setScientificName(e.target.value)} value={nickName} />
                    <Form.Input fluid label='Nickname' type="text" onChange={(e) => setNickName(e.target.value)} value={nickName}  />
                </Form.Group>
                <div className="formRadio">
                <Form.Group inline>
                    <label>Light</label>
                    <Form.Radio
                        label='low'
                        value='low'
                        // checked={value === 'sm'}
                        // onChange={this.handleChange}
                    />
                    <Form.Radio
                        label='medium'
                        value='medium'
                        // checked={value === 'md'}
                        // onChange={this.handleChange}
                    />
                    <Form.Radio
                        label='high'
                        value='high'
                        // checked={value === 'lg'}
                        // onChange={this.handleChange}
                    />
                    </Form.Group>
                    <Form.Group inline >
                    <label className="waterRadio">Water</label>
                    <Form.Radio
                        label='low'
                        value='low'
                        // checked={value === 'sm'}
                        // onChange={this.handleChange}
                    />
                    <Form.Radio
                        label='medium'
                        value='medium'
                        // checked={value === 'md'}
                        // onChange={this.handleChange}
                    />
                    <Form.Radio
                        label='high'
                        value='high'
                        // checked={value === 'lg'}
                        // onChange={this.handleChange}
                    />
                </Form.Group>
                </div>
                <Form.TextArea label='About' placeholder='Tell us more about you...' />
                <Button className="yellow large" style={{ "marginTop": "10px" }}>Submit</Button>
            </Form>

        </div>
    )
}
