import React, { useEffect, useState } from 'react';
import { Link } from '@reach/router';
import { Button, Form } from 'semantic-ui-react';
// import SearchBox from '../components/SearchBox';
// import Search from '../components/Search';
import axios from 'axios';


const LandingPage = () => {

    const [plants, setPlants] = useState([]);
    const [search, setSearch] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);


    const getPlants = () => {
        axios.get(`https://cors-anywhere.herokuapp.com/https://trefle.io/api/v1/species/search?q=${search}&token=wfOfrblXggceP_xcDAcmmxDOO4XmIyJY1KgXKK0_utA`)
            .then(res => {
                if (res.data.data.length > 0) {
                    setPlants(res.data.data);
                    console.log(res.data);
                } else {
                    console.log("error message");
                    setErrorMessage("Please try another keyword.");
                }
            })
    }

    const searchPlants = (e) => {
        e.preventDefault();
        getPlants();
    };


    return (
        <div className="wrapper">
            <header>
                <h1>Planthood </h1>
                <div className="button">
                    <Button className="ui green basic button large"><Link style={{ "color": "green" }} to={"/new"}>Log In</Link></Button>
                    <Button className="ui green basic button large"><Link style={{ "color": "green" }} to={"/"}>Sign Up</Link></Button>
                </div>
            </header>
            <div className="landingDisplay">

                {/* <form className="search" stlye={{ "display": "flex" }}>
                    <Search
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                        type="text"
                    />
                    <Button onClick={searchPlants} type="submit">Search</Button>
                </form> */}

                <Form className="searchBox">
                    <Form.Group>
                        <Form.Input
                            placeholder='ex) parlor palm'
                            name='name'
                            value={search}
                            onChange={(e) => setSearch(e.target.value)}
                        />
                        <Button onClick={searchPlants} className="teal"  content='Submit' />
                    </Form.Group> </Form>

                    {/* <Button onClick={searchPlants} className="teal">Search Plants</Button> */}
                    <h2>Search Results</h2>

                    {errorMessage ? <div style={{ "color": "red" }}> {errorMessage} </div> : null}

                    <div className="list">
                        {plants.map((value, index) => {
                            return (<h3 className="plantList" plant={value} key={index} > 
                            {value.common_name}
                            {value.scientific_name} 
                            <img width="100" src={value.image_url} alt="tryin"></img></h3>)
                        })}
                    </div> 
            </div>
            </div>

    )
}

export default LandingPage
