import React, { useEffect, useState } from "react";
import Header from '../components/Header';
import './PlantDetail.css';
import axios from 'axios';

const PlantDetail = (props) => {

    const [plant, setPlant] = useState();
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        axios.get(`http://localhost:8050/api/plants/${props.id}`)
            .then(res => {
                setPlant(res.data)
                setLoaded(true);
            })
            .catch(err => console.log(err));
    }, [])

    return (
        <div className="wrapper">
            <Header />
            {loaded && (
            <div>
            <h1 style={{"marginBottom":"20px"}}>{plant.nickname}</h1>
            <h4>Genre: {plant.name}</h4>
            <h4>Notes: {plant.scientificName}</h4>
            <h4>Notes: {plant.water}</h4>
            <h4>Notes: {plant.light}</h4>
            <h4>Notes: {plant.info}</h4>
            <img src={plant.image} alt="" width="200" />
            {/* <h4>Description: {author.description}</h4> */}
            </div>
            )}
        </div>
    )
}

export default PlantDetail;
